js:
$(document).ready(function() {
    $.get("server.php", function(data) {
        $("#result").append(data);
    })
});

$("form").on("submit", function(e) {
    e.preventDefault();
    let formData = $(this).serialize();
    $.ajax({
        type: "POST",
        data: formData,
        url: "server.php",
        success: function(data) {
            $("#result").empty().append(data);
            $("textarea").val("").focus();
        }
    })
})}

html:
&lt;form action="backend.php" method="POST"&gt;
